struct cs_info * get_current_cs(const char * es) {
  struct cs_info * ccs = encds[0].cs_table;
  int n = sizeof(encds) / sizeof(encds[0]);
  for (int i = 0; i < n; i++) {
    if (strcmp(es,encds[i].enc_name) == 0) {
      ccs = encds[i].cs_table;
    }
  }
  return ccs;
};


